sys_hub_input_scripts.script:
{"total_allowance":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value. 
**Order number is offset by +1 in Error Handling Section.
**Available options display upon pressing "." after fd_data
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/
var total = 0;
var flightDetails=fd_data._6__get_catalog_variables.flight_details;

flightDetails.forEach(flight => {
 var allowance = parseFloat(flight.allowance);
 var currency = flight.allowance_type; // Use select_currency field for currency
gs.info("selected currency is" +currency);
 if (!isNaN(allowance)) {
 var allowanceInUSD = convertToUSD(allowance, currency);
 total += allowanceInUSD;
 }
});

return total;

function convertToUSD(amount, currency) {
 if (currency === 'USD') {
 return amount;
 }

 var converter = new sn_currency.GlideCurrencyConverter(currency, 'USD');
 converter.setAmount(amount);
gs.info('currency is'+currency);
 // Convert the amount to the target currency
 var convertedAmountObj = converter.convert();
 var convertedAmount = convertedAmountObj.getAmount();

 return parseFloat(convertedAmount);
}","savedValue":""}}

